import React, { useState } from 'react';
import Monsters from './monsters'
import Equipments from './equipments'

// children
const Header = ({ShowEquipment, ShowMonsters}) => {
    return ( 
        <header>
            <nav>
                <a href="#" onClick={() => ShowMonsters()}>Monsters</a>
                <a href="#" onClick={() => ShowEquipment()}>Equipments</a>
            </nav>
        </header>
    );
}

const Main = () => {

    const [content, setContent] = useState('Monsters');

    function ShowEquipment () {
        setContent('Equipments')
    }
    function ShowMonsters () {
        setContent('Monsters')
    }

    if (content === 'Monsters') {
        return ( 
            <>
            <Header ShowEquipment={ShowEquipment} ShowMonsters={ShowMonsters}/>
            <Monsters/>
            </>
        );
    } 

    if (content === 'Equipments') {
        return (
            <>
            <Header ShowEquipment={ShowEquipment} ShowMonsters={ShowMonsters}/>
            <Equipments/>
            </>
        )
    }

}

export default Main;
