            <div className="treasure-list" key={treasure.id}>
                <h1>{treasure.name.toUpperCase()}</h1>
                <img src={treasure.image} alt={tr} />
                <p>{treasure.description}</p>
                <ul>
                    Common Locations {treasure.common_locations.map( location => <li>{location}</li>)}
                </ul>
                <ul>
                    Drops {treasure.drops.map(drop => <li>{drop.charAt(0).toUpperCase() + drop.slice(1)}</li>)}
                </ul>
            </div>

                )}
        </div>
    );
}

